// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import './lib/EasyMath.sol';

contract InterestRateModel {
    using EasyMath for int256;

    uint256 public constant ACCEPTED_RANGE = 5e16;
    int256 public constant DECIMAL_POINTS = 1e18;

    uint256 public updateInterval;

    struct PIDParams {
        int256 utilizationOptimal;
        int256 kp;
    }

    PIDParams public pidParams;

    constructor(int256 _utilizationOptimal, int256 _kp, uint256 _updateInterval) {
        pidParams = PIDParams(_utilizationOptimal, _kp);
        updateInterval = _updateInterval;
    }

    function getInterestRate(
        uint256 debt,
        uint256 liquidity,
        uint256 previousInterestRate,
        uint256 lastUpdateTimestamp
    ) external view returns (uint256, uint256) {
        uint256 timeDelta = block.timestamp - lastUpdateTimestamp;
        if (timeDelta < updateInterval || liquidity == 0) return (previousInterestRate, lastUpdateTimestamp);

        int256 u = int256(debt) * DECIMAL_POINTS / int256(liquidity);

        int256 error = u - pidParams.utilizationOptimal;
        if (error.abs() <= ACCEPTED_RANGE) {
            error = 0;
        }
        int256 p = pidParams.kp * error / DECIMAL_POINTS;
        int256 newInterestRate = int256(previousInterestRate) + p.toBasisPoint();

        if (newInterestRate < 0 || debt == 0) {
            newInterestRate = 0;
        }
        return (uint256(newInterestRate), block.timestamp);
    }
}

contracts/lib/EasyMath.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

library EasyMath {
    function abs(int256 x) internal pure returns (uint256) {
        return x <= 0 ? uint256(-x) : uint256(x);
    }

    function toShare(uint256 amount, uint256 totalAmount, uint256 totalShares) internal pure returns (uint256) {
        if (totalShares == 0 || totalAmount == 0) {
            return amount;
        }
        return amount * totalShares / totalAmount;
    }

    function toAmount(uint256 share, uint256 totalAmount, uint256 totalShares) internal pure returns (uint256) {
        if (totalShares == 0 || totalAmount == 0) {
            return 0;
        }
        return share * totalAmount / totalShares;
    }

    /// from 18 decimals to basis points (4 decimals)
    function toBasisPoint(uint256 amount) internal pure returns (uint256) {
        return amount / 1e14;
    }

    /// from basis points (4 decimals) ot 18 decimals
    function fromBasisPoint(uint256 amount) internal pure returns (uint256) {
        return amount * 1e14;
    }

    /// from 18 decimals to basis points (4 decimals)
    function toBasisPoint(int256 amount) internal pure returns (int256) {
        return amount / 1e14;
    }

    /// from basis points (4 decimals) ot 18 decimals
    function fromBasisPoint(int256 amount) internal pure returns (int256) {
        return amount * 1e14;
    }
}

contracts/SiloBridgePool.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import './interfaces/ISiloBridgePool.sol';
import './interfaces/ISiloFactory.sol';
import './interfaces/IInterestRateModel.sol';
import './interfaces/ISilo.sol';
import './interfaces/ISiloOracle.sol';
import './interfaces/ISiloCallee.sol';

import './utils/BridgeERC20.sol';
import './lib/EasyMath.sol';

contract SiloBridgePool is ISiloBridgePool, ReentrancyGuard {
    using SafeERC20 for BridgeERC20;
    using SafeERC20 for ERC20;
    using EasyMath for uint256;

    uint256 public constant SECONDS_PER_YEAR = 31536000;
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant INFINITY = type(uint256).max;

    uint256 public immutable DECIMAL_POINTS;
    ISiloFactory immutable public factory;

    address public override asset;

    struct BridgeSiloStorage {
        BridgeERC20 token;
        uint256 lastUpdateTimestamp;
        uint256 totalDeposits;
        uint256 totalBorrowShare;
        uint256 totalBorrowAmount;
        uint256 interestRate;
        uint256 protocolFees;
        uint256 liquidity;
        mapping(address => uint256) borrowShare;
    }
    // silo address => BridgeSiloStorage
    mapping(address => BridgeSiloStorage) public state;

    event Deposit(address indexed silo, address indexed user, uint256 amount);
    event Withdraw(address indexed silo, address indexed user, uint256 amount);
    event Borrow(address indexed silo, address indexed user, uint256 amount);
    event Repay(address indexed silo, address indexed user, uint256 amount);
    event Liquidate(address indexed silo, address indexed _user, uint256 amountRepaid, uint256 seizedCollateral);

    modifier onlyRouter() {
        require(msg.sender == factory.router(), "SiloBridgePool: onlyRouter");
        _;
    }

    modifier onlyFactory() {
        require(msg.sender == address(factory), "SiloBridgePool: onlyFactory");
        _;
    }

    modifier onlySilo() {
        require(factory.isSilo(msg.sender), "SiloBridgePool: onlySilo");
        _;
    }

    modifier interest(address _silo) {
        accrueInterest(_silo);
        _;
    }

    /// @dev check if user has enough collateral deposited to bridge pool to support withstanding borrow positon on silo
    modifier siloPositionSolvent(address _silo, address _user) {
        _;
        require(ISilo(_silo).isSolvent(_user), "SiloBridgePool: not solvernt in brigde position");
    }

    modifier validateBorrow(address _silo, address _user) {
        _;
        require(
            getCollateralization(_silo, _user) >= factory.getBorrowCollateralization(address(this)),
            "SiloBridge: !borrowCollateralization"
        );
    }

    constructor (address _factory) {
        factory = ISiloFactory(_factory);
        asset = ISiloFactory(_factory).bridgeAsset();
        DECIMAL_POINTS = 10**ERC20(asset).decimals();
    }

    function liquidity() public view returns (uint256) {
        return BridgeERC20(asset).balanceOf(address(this));
    }

    function balanceOfUnderlying(address _silo, address _user) public view returns (uint256) {
        BridgeSiloStorage storage _state = state[_silo];
        uint256 share = _state.token.balanceOf(_user);
        return share.toAmount(_state.totalDeposits, _state.token.totalSupply());
    }

    function calcFee(uint256 _amount) public view returns (uint256) {
        uint256 entryFee = factory.entryFee();
        if (entryFee == 0) return 0; // no fee
        return _amount * entryFee / BASIS_POINTS;
    }

    function isSolvent(address _silo, address _user) public override returns (bool) {
        require(_user != address(0), "SiloBridgePool: user != address(0)");
        uint256 minimumCollateralization = factory.getMinCollateralization(address(this));
        return getCollateralization(_silo, _user) >= minimumCollateralization;
    }

    /// @dev ratio between value borrowed by user in this silo to collateral value provided in bridge pool for this silo
    function getCollateralization(address _silo, address _user) public override returns (uint256) {
        uint256 debtValue = getDebtValue(_silo, _user);
        if (debtValue == 0) return INFINITY;

        uint256 collateralValue = ISilo(_silo).getCollateralValue(_user);
        return collateralValue * BASIS_POINTS / debtValue;
    }

    /// @dev value of collateral (asset) provided by user
    function getCollateralValue(address _silo, address _user) public override returns (uint256) {
        BridgeSiloStorage storage _state = state[_silo];
        ISiloOracle oracle = ISiloOracle(factory.oracle());
        uint256 assetPrice = oracle.getPrice(asset);
        uint256 assetAmount = _state.token.balanceOf(_user).toAmount(
            _state.totalDeposits,
            _state.token.totalSupply()
        );
        // TODO: price must have 1e18 decimals
        return assetAmount * assetPrice / DECIMAL_POINTS;
    }

    /// @dev value of asset borrowed by user
    function getDebtValue(address _silo, address _user) public returns (uint256) {
        BridgeSiloStorage storage _state = state[_silo];
        ISiloOracle oracle = ISiloOracle(factory.oracle());
        uint256 assetDebtAmount = _state.borrowShare[_user].toAmount(
            _state.totalBorrowAmount,
            _state.totalBorrowShare
        );
        uint256 assetPrice = oracle.getPrice(asset);
        // TODO: price must have 1e18 decimals
        return assetDebtAmount * assetPrice / DECIMAL_POINTS;
    }

    function getBorrowShare(address _silo, address _user) public view returns (uint256) {
        BridgeSiloStorage storage _state = state[_silo];
        return _state.borrowShare[_user];
    }

    function deposit(address _silo, uint256 _amount) external {
        _deposit(_silo, msg.sender, msg.sender, _amount);
    }

    function depositFor(address _silo, address _user, uint256 _amount) external override onlyRouter {
        _deposit(_silo, _user, msg.sender, _amount);
    }

    function _deposit(address _silo, address _user, address _from, uint256 _amount)
        internal
        nonReentrant
        interest(_silo)
    {
        uint256 balanceBefore = liquidity();
        ERC20(asset).safeTransferFrom(_from, address(this), _amount);
        uint256 balanceAfter = liquidity();

        BridgeSiloStorage storage _state = state[_silo];
        _amount = balanceAfter - balanceBefore;
        uint256 share = _amount.toShare(_state.totalDeposits, _state.token.totalSupply());
        _state.totalDeposits += _amount;
        _state.liquidity += _amount;

        _state.token.mint(_user, share);
        emit Deposit(_silo, _user, _amount);
    }

    function withdraw(address _silo, uint256 _amount) external {
        _withdraw(_silo, msg.sender, _amount);
    }

    function withdrawFor(address _silo, address _user, uint256 _amount) external onlyRouter {
        _withdraw(_silo, _user, _amount);
    }

    function _withdraw(address _silo, address _user, uint256 _amount)
        internal
        nonReentrant
        siloPositionSolvent(_silo, _user)
        interest(_silo)
    {
        BridgeSiloStorage storage _state = state[_silo];
        uint256 share;
        if (_amount == type(uint256).max) {
            share = _state.token.balanceOf(_user);
            _amount = share.toAmount(_state.totalDeposits, _state.token.totalSupply());
        } else {
            share = _amount.toShare(_state.totalDeposits, _state.token.totalSupply());
        }
        _state.totalDeposits -= _amount;
        _state.liquidity -= _amount;
        _state.token.burn(_user, share);
        emit Withdraw(_silo, _user, _amount);

        BridgeERC20(asset).safeTransfer(_user, _amount);
    }

    function borrow(address _silo, uint256 _amount)
        external
        validateBorrow(_silo, msg.sender)
        interest(_silo)
    {
        _borrow(_silo, msg.sender, msg.sender, _amount);
    }

    function borrowFor(address _silo, address _user, address _to, uint256 _amount)
        external
        override
        onlyRouter
        validateBorrow(_silo, _user)
        interest(_silo)
    {
        _borrow(_silo, _user, _to, _amount);
    }

    function flashBorrow(address _silo, uint256 _amount, address _to, bytes memory _data)
        external
        validateBorrow(_silo, msg.sender)
        interest(_silo)
    {
        _borrow(_silo, msg.sender, msg.sender, _amount);

        ISiloCallee(_to).siloCall(msg.sender, _amount, _data);
    }

    function _borrow(address _silo, address _user, address _to, uint256 _amount) internal nonReentrant {
        BridgeSiloStorage storage _state = state[_silo];
        require(_state.liquidity >= _amount, "SiloBridge: liquidity shortage");

        uint256 fee = calcFee(_amount);
        uint256 borrowerShare = (_amount + fee).toShare(_state.totalBorrowAmount, _state.totalBorrowShare);
        _state.borrowShare[_user] += borrowerShare;
        _state.totalBorrowShare += borrowerShare;
        _state.totalBorrowAmount += _amount + fee;
        _state.protocolFees += fee;
        _state.liquidity -= _amount;

        emit Borrow(_silo, _user, _amount);

        BridgeERC20(asset).safeTransfer(_to, _amount);
    }

    function repay(address _silo, uint256 _amount) external {
        _repay(_silo, msg.sender, msg.sender, _amount);
    }

    function repayFor(address _silo, address _user, uint256 _amount) external onlyRouter {
        _repay(_silo, _user, msg.sender, _amount);
    }

    function _repay(address _silo, address _user, address _from, uint256 _amount) internal nonReentrant interest(_silo) returns (uint256) {
        BridgeSiloStorage storage _state = state[_silo];
        uint256 repayShare;
        if (_amount == type(uint256).max) {
            repayShare = _state.borrowShare[_user];
            _amount = repayShare.toAmount(_state.totalBorrowAmount, _state.totalBorrowShare);
        } else {
            repayShare = _amount.toShare(_state.totalBorrowAmount, _state.totalBorrowShare);
        }

        _state.borrowShare[_user] -= repayShare;
        _state.totalBorrowShare -= repayShare;
        _state.totalBorrowAmount -= _amount;
        _state.liquidity += _amount;

        emit Repay(_silo, _user, _amount);

        BridgeERC20(asset).safeTransferFrom(_from, address(this), _amount);
        return _amount;
    }

    function seizeCollateral(address _user, address _liquidator) external override onlySilo returns (uint256) {
        BridgeSiloStorage storage _state = state[msg.sender];
        uint256 collateral = _state.token.balanceOf(_user);
        _state.token.forceTransfer(_user, _liquidator, collateral);
        return collateral.toAmount(_state.totalDeposits, _state.token.totalSupply());
    }

    function liquidate(address _silo, address[] memory _users) external interest(_silo) returns (uint256[] memory) {
        uint256[] memory liquidated = new uint256[](_users.length);
        uint256 n = _users.length;
        for (uint256 i = 0; i < n; i++) {
            liquidated[i] = _liquidate(_silo, _users[i]);
        }
        return liquidated;
    }

    function _liquidate(address _silo, address _user) internal nonReentrant returns (uint256 seizedCollateral) {
        // gracefully fail if _user is solvent
        if (isSolvent(_silo, _user)) return 0;

        uint256 amountRepaid = _repay(_silo, _user, msg.sender, type(uint256).max);
        seizedCollateral = ISilo(_silo).seizeCollateral(_user, msg.sender);

        emit Liquidate(_silo, _user, amountRepaid, seizedCollateral);

        require(isSolvent(_silo, _user), "SiloBridgePool: user not solvent");
    }

    function accrueInterest(address _silo) public {
        BridgeSiloStorage storage _state = state[_silo];

        uint256 timeDelta = block.timestamp - _state.lastUpdateTimestamp;
        if (timeDelta == 0) return;

        // accrue interest
        uint256 totalInterest = _state.totalBorrowAmount * _state.interestRate * timeDelta / BASIS_POINTS / SECONDS_PER_YEAR;
        uint256 protocolInterestShare = totalInterest * factory.protocolShareFee() / BASIS_POINTS;
        uint256 siloInterestShare = totalInterest - protocolInterestShare;
        _state.totalBorrowAmount += totalInterest;
        _state.protocolFees += protocolInterestShare;
        _state.totalDeposits += siloInterestShare;

        // update interest rate
        address model = factory.getInterestRateModel(address(this));

        (_state.interestRate,_state.lastUpdateTimestamp) = IInterestRateModel(model).getInterestRate(
            _state.totalBorrowAmount,
            _state.totalDeposits,
            _state.interestRate,
            _state.lastUpdateTimestamp
        );
    }

    function addSilo(address _silo, string memory _name, string memory _symbol) external override onlyFactory {
        BridgeSiloStorage storage _state = state[_silo];
        string memory name = string(abi.encodePacked(string('Bridge '), _name));
        string memory symbol = string(abi.encodePacked(string('bridge-'), _symbol));
        // TODO: optimize BridgeERC20 deployment
        _state.token = new BridgeERC20(name, symbol);
    }
}

