    mapping(address => address) public override silo;
    /// @dev maps token address to silo address, silo => token
    mapping(address => address) public siloReverse;

    event NewSilo(address indexed silo, address indexed asset, string name, string symbol);
    event InterestRateModel(address oldModel, address newModel);
    event BridgePoolUpdate(address oldPool, address newPool);
    event OracleUpdate(address oldOracle, address newOracle);
    event RouterUpdate(address oldRouter, address newRouter);
    event FeeUpdate(
        uint256 oldEntryFee,
        uint256 newEntryFee,
        uint256 oldProtocolShareFee,
        uint256 newProtocolShareFee,
        uint256 oldProtocolLiquidationFee,
        uint256 newProtocolLiquidationFee
    );

    constructor(
        uint256 _borrowCollateralization,
        uint256 _minCollateralization,
        address _bridgeAsset
    ) {
        borrowCollateralization = _borrowCollateralization;
        minCollateralization = _minCollateralization;
        bridgeAsset = _bridgeAsset;
    }

    function newSilo(address _asset, address _assetOracle, bytes memory _oracleData) public {
        require(_asset != bridgeAsset, "SiloFactory: bridge asset");
        require(silo[_asset] == address(0), "SiloFactory: silo exists");

        string memory name = string(abi.encodePacked(string('Silo '), ERC20(_asset).name()));
        string memory symbol = string(abi.encodePacked(string('silo'), ERC20(_asset).symbol()));
        Silo _silo = new Silo(_asset, name, symbol);
        silo[_asset] = address(_silo);
        siloReverse[address(_silo)] = _asset;
        emit NewSilo(address(_silo), _asset, name, symbol);

        ISiloBridgePool(bridgePool).addSilo(address(_silo), name, symbol);
        ISiloOracle(oracle).registerToken(_asset, _assetOracle, _oracleData);
    }

    function isSilo(address _silo) external view override returns (bool) {
        return siloReverse[_silo] != address(0);
    }

    // GETTERS
    // TODO: support unique config for each silo

    function getBorrowCollateralization(address _silo) external view override returns (uint256) {
        _silo;
        return borrowCollateralization;
    }

    function getInterestRateModel(address _silo) external view override returns (address) {
        _silo;
        return interestRateModel;
    }

    function getMinCollateralization(address _silo) external view override returns (uint256) {
        _silo;
        return minCollateralization;
    }

    // ADMIN

    function setFees(uint256 _entryFee, uint256 _protocolShareFee, uint256 _protocolLiquidationFee) external onlyOwner {
        emit FeeUpdate(entryFee, _entryFee, protocolShareFee , _protocolShareFee, protocolLiquidationFee, _protocolLiquidationFee);

        entryFee = _entryFee;
        protocolShareFee = _protocolShareFee;
        protocolLiquidationFee = _protocolLiquidationFee;
    }

    function setInterestRateModel(address _interestRateModel) external onlyOwner {
        require(_interestRateModel != address(0), "SiloFactory: new interestRateModel is zero address");
        emit InterestRateModel(interestRateModel, _interestRateModel);
        interestRateModel = _interestRateModel;
    }

    function setBridgePool(address _bridgePool) external onlyOwner {
        require(_bridgePool != address(0), "SiloFactory: new bridgePool is zero address");
        emit BridgePoolUpdate(bridgePool, _bridgePool);
        bridgePool = _bridgePool;
    }

    function setOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "SiloFactory: new bridgePool is zero address");
        emit OracleUpdate(oracle, _oracle);
        oracle = _oracle;
    }

    function setRouter(address _router) external onlyOwner {
        require(_router != address(0), "SiloFactory: new bridgePool is zero address");
        emit RouterUpdate(router, _router);
        router = _router;
    }
}

contracts/Silo.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';

import './interfaces/ISilo.sol';
import './interfaces/ISiloFactory.sol';
import './interfaces/ISiloBridgePool.sol';
import './interfaces/IInterestRateModel.sol';
import './interfaces/ISiloCallee.sol';
import './interfaces/ISiloOracle.sol';

import './lib/EasyMath.sol';

contract Silo is ISilo, ERC20, ReentrancyGuard {
    using SafeERC20 for ERC20;
    using EasyMath for uint256;

    uint256 public constant SECONDS_PER_YEAR = 31536000;
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant INFINITY = type(uint256).max;

    uint256 public immutable DECIMAL_POINTS;
    ISiloFactory immutable public factory;

    address public override asset;
    uint256 public lastUpdateTimestamp;
    uint256 public totalDeposits;
    uint256 public totalBorrowShare;
    uint256 public totalBorrowAmount;
    uint256 public interestRate;
    uint256 public protocolFees;

    mapping(address => uint256) public borrowShare;

    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);
    event Borrow(address indexed user, uint256 amount);
    event Repay(address indexed user, uint256 amount);
    event Liquidate(address indexed _user, uint256 amountRepaid, uint256 seizedCollateral);

    modifier onlyRouter() {
        require(msg.sender == factory.router(), "Silo: onlyRouter");
        _;
    }

    modifier onlyBridge() {
        require(msg.sender == factory.bridgePool(), "Silo: onlyBridge");
        _;
    }

    modifier interest() {
        accrueInterest();
        _;
    }

    /// @dev check if user has enough collateral deposited to silo to support withstanding borrow positon on bridgePool
    modifier bridgePositionSolvent(address _user) {
        _;
        ISiloBridgePool bridgePool = ISiloBridgePool(factory.bridgePool());
        require(bridgePool.isSolvent(address(this), _user), "Silo: not solvent in brigde position");
    }

    modifier validateBorrow(address _user) {
        _;
        require(
            getCollateralization(_user) >= factory.getBorrowCollateralization(address(this)),
            "Silo: !borrowCollateralization"
        );
    }

    constructor (
        address _asset,
        string memory _name,
        string memory _symbol
    ) ERC20(_name, _symbol) {
        factory = ISiloFactory(msg.sender);
        asset = _asset;
        DECIMAL_POINTS = 10**ERC20(_asset).decimals();
    }

    function liquidity() public view returns (uint256) {
        return ERC20(asset).balanceOf(address(this));
    }

    function balanceOfUnderlying(address _user) public view returns (uint256) {
        uint256 share = balanceOf(_user);
        return share.toAmount(totalDeposits, totalSupply());
    }

    function calcFee(uint256 _amount) public view returns (uint256) {
        uint256 entryFee = factory.entryFee();
        if (entryFee == 0) return 0; // no fee
        return _amount * entryFee / BASIS_POINTS;
    }

    function isSolvent(address _user) public override returns (bool) {
        require(_user != address(0), "Silo: user != address(0)");
        uint256 minimumCollateralization = factory.getMinCollateralization(address(this));
        return getCollateralization(_user) >= minimumCollateralization;
    }

    /// @dev ratio between value borrowed by user in this silo to collateral value provided in bridge pool for this silo
    function getCollateralization(address _user) public returns (uint256) {
        uint256 debtValue = getDebtValue(_user);
        if (debtValue == 0) return INFINITY;

        ISiloBridgePool bridgePool = ISiloBridgePool(factory.bridgePool());
        uint256 collateralValue = bridgePool.getCollateralValue(address(this), _user);
        return collateralValue * BASIS_POINTS / debtValue;
    }

    /// @dev value of collateral (asset) provided by user
    function getCollateralValue(address _user) public override returns (uint256) {
        ISiloOracle oracle = ISiloOracle(factory.oracle());
        uint256 assetPrice = oracle.getPrice(asset);
        uint256 assetAmount = balanceOf(_user).toAmount(totalDeposits, totalSupply());
        // TODO: price must have 1e18 decimals
        return assetAmount * assetPrice / DECIMAL_POINTS;
    }

    /// @dev value of asset borrowed by user
    function getDebtValue(address _user) public returns (uint256) {
        ISiloOracle oracle = ISiloOracle(factory.oracle());
        uint256 assetDebtAmount = borrowShare[_user].toAmount(totalBorrowAmount, totalBorrowShare);
        uint256 assetPrice = oracle.getPrice(asset);
        // TODO: price must have 1e18 decimals
        return assetDebtAmount * assetPrice / DECIMAL_POINTS;
    }

    function deposit(uint256 _amount) external override {
        _deposit(msg.sender, msg.sender, _amount);
    }

    function depositFor(address _user, uint256 _amount) external override onlyRouter {
        _deposit(_user, msg.sender, _amount);
    }

    function _deposit(address _user, address _from, uint256 _amount) internal nonReentrant interest {
        require(borrowShare[_user] == 0, "Silo: cannot deposit & borrow the same asset");

        uint256 balanceBefore = liquidity();
        ERC20(asset).safeTransferFrom(_from, address(this), _amount);
        uint256 balanceAfter = liquidity();

        _amount = balanceAfter - balanceBefore;
        uint256 share = _amount.toShare(totalDeposits, totalSupply());
        totalDeposits += _amount;
        emit Deposit(_user, _amount);
        _mint(_user, share);
    }

    function withdraw(uint256 _amount) external override {
        _withdraw(msg.sender, _amount);
    }

    function withdrawFor(address _user, uint256 _amount) external override onlyRouter {
        _withdraw(_user, _amount);
    }

    function _withdraw(address _user, uint256 _amount) internal nonReentrant bridgePositionSolvent(_user) interest {
        uint256 share;
        if (_amount == type(uint256).max) {
            share = balanceOf(_user);
            _amount = share.toAmount(totalDeposits, totalSupply());
        } else {
            share = _amount.toShare(totalDeposits, totalSupply());
        }
        totalDeposits -= _amount;
        _burn(_user, share);
        emit Withdraw(_user, _amount);

        ERC20(asset).safeTransfer(_user, _amount);
    }

    function borrow(uint256 _amount) external override validateBorrow(msg.sender) interest  {
        _borrow(msg.sender, msg.sender, _amount);
    }

    function borrowFor(address _user, address _to, uint256 _amount) external override onlyRouter validateBorrow(_user) interest {
        _borrow(_user, _to, _amount);
    }

    function flashBorrow(uint256 _amount, address _to, bytes memory _data)
        external
        validateBorrow(msg.sender)
        interest
    {
        _borrow(msg.sender, msg.sender, _amount);

        ISiloCallee(_to).siloCall(msg.sender, _amount, _data);
    }

    function _borrow(address _user, address _to, uint256 _amount) internal nonReentrant {
        require(balanceOf(_user) == 0, "Silo: cannot borrow & deposit the same asset");
        require(liquidity() >= _amount, "Silo: liquidity shortage");

        uint256 fee = calcFee(_amount);
        uint256 borrowerShare = (_amount + fee).toShare(totalBorrowAmount, totalBorrowShare);
        borrowShare[_user] += borrowerShare;
        totalBorrowShare += borrowerShare;
        totalBorrowAmount += _amount + fee;
        protocolFees += fee;

        emit Borrow(_user, _amount);

        ERC20(asset).safeTransfer(_to, _amount);
    }

    function repay(uint256 _amount) external override nonReentrant {
        _repay(msg.sender, msg.sender, _amount);
    }

    function repayFor(address _user, uint256 _amount) external override nonReentrant onlyRouter {
        _repay(_user, msg.sender, _amount);
    }

    function _repay(address _user, address _from, uint256 _amount) internal interest returns (uint256) {
        uint256 repayShare;
        if (_amount == type(uint256).max) {
            repayShare = borrowShare[_user];
            _amount = repayShare.toAmount(totalBorrowAmount, totalBorrowShare);
        } else {
            repayShare = _amount.toShare(totalBorrowAmount, totalBorrowShare);
        }

        borrowShare[_user] -= repayShare;
        totalBorrowShare -= repayShare;
        totalBorrowAmount -= _amount;

        emit Repay(_user, _amount);

        ERC20(asset).safeTransferFrom(_from, address(this), _amount);
        return _amount;
    }

    function seizeCollateral(address _user, address _liquidator) external override onlyBridge returns (uint256) {
        uint256 collateral = balanceOf(_user);
        _transfer(_user, _liquidator, collateral);
        return collateral.toAmount(totalDeposits, totalSupply());
    }

    function liquidate(address[] memory _users) external override nonReentrant interest returns (uint256[] memory) {
        uint256[] memory liquidated = new uint256[](_users.length);
        uint256 n = _users.length;
        for (uint256 i = 0; i < n; i++) {
            liquidated[i] = _liquidate(_users[i]);
        }
        return liquidated;
    }

    function _liquidate(address _user) internal returns (uint256 seizedCollateral) {
        // gracefully fail if _user is solvent
        if (isSolvent(_user)) return 0;

        uint256 amountRepaid = _repay(_user, msg.sender, type(uint256).max);

        ISiloBridgePool bridgePool = ISiloBridgePool(factory.bridgePool());
        seizedCollateral = bridgePool.seizeCollateral(_user, msg.sender);

        emit Liquidate(_user, amountRepaid, seizedCollateral);

        require(isSolvent(_user), "Silo: user not solvent");
    }

    function accrueInterest() public {
        uint256 timeDelta = block.timestamp - lastUpdateTimestamp;
        if (timeDelta == 0) return;

        // accrue interest
        uint256 totalInterest = totalBorrowAmount * interestRate * timeDelta / BASIS_POINTS / SECONDS_PER_YEAR;
        uint256 protocolInterestShare = totalInterest * factory.protocolShareFee() / BASIS_POINTS;
        uint256 siloInterestShare = totalInterest - protocolInterestShare;
        totalBorrowAmount += totalInterest;
        protocolFees += protocolInterestShare;
        totalDeposits += siloInterestShare;

        // update interest rate
        address model = factory.getInterestRateModel(address(this));
        (interestRate, lastUpdateTimestamp) = IInterestRateModel(model).getInterestRate(
            totalBorrowAmount,
            totalDeposits,
            interestRate,
            lastUpdateTimestamp
        );
    }
}

contracts/SiloOracle.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import './interfaces/ISiloOracle.sol';
import './interfaces/IOracle.sol';

contract SiloOracle is ISiloOracle {
    uint256 public constant ONE = 10**18;

    address public immutable factory;
    address public quoteToken;

    struct OracleData {
        address oracleImpl;
        bytes data;
    }

    mapping(address => OracleData) public oracles;

    modifier onlyFactory() {
        require(msg.sender == address(factory), "SiloOracle: onlyFactory");
        _;
    }

    constructor(address _quoteToken, address _factory) {
        factory = _factory;
        quoteToken = _quoteToken;
    }

    function registerToken(address _token, address _oracle, bytes memory _data) external override onlyFactory {
        require(IOracle(_oracle).isSiloOracle(), "SiloOracle: not an oracle");
        oracles[_token] = OracleData(_oracle, _data);
        IOracle(_oracle).initToken(_token, quoteToken, _data);
    }

    function getPrice(address _token) public virtual override returns (uint256) {
        return getPriceQuote(_token, quoteToken);
    }

    function getPriceQuote(address _base, address _quote) public returns (uint256) {
        if (_base == _quote) return ONE;
        return IOracle(oracles[_base].oracleImpl).getPrice(_base, _quote, oracles[_base].data);
    }
}

