// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import '@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol';

import './Oracle.sol';
/// @dev expects ETH feeds
contract ChainlinkOracle is Oracle {
    uint256 public constant DECIMALS = 10**18;

    function getPrice(address, address, bytes memory _data) public view virtual override returns (uint256) {
        address priceFeed = abi.decode(_data, (address));
        // TODO: safe casting
        (int256 price, ) = getLatestPrice(priceFeed);
        // TODO: check a timestamp to make sure the price is fresh
        return uint256(price) * DECIMALS / 10 ** AggregatorV3Interface(priceFeed).decimals();
    }

    function getLatestPrice(address priceFeed) public view returns (int256 price, uint256 timeStamp) {
        (, price,, timeStamp,) = AggregatorV3Interface(priceFeed).latestRoundData();
    }
}

