// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import './ChainlinkOracle.sol';

/// @dev converts USD feeds to ETH
contract ChainlinkOracleUSD is ChainlinkOracle {
    address public immutable ethPriceFeed;

    constructor(address _ethPriceFeed) {
        ethPriceFeed = _ethPriceFeed;
    }

    function getPrice(address _base, address _quote, bytes memory _data) public view override returns (uint256) {
        return fromUSDtoETH(super.getPrice(_base, _quote, _data));
    }

    function fromUSDtoETH(uint256 usd) public view returns (uint256) {
        (, int256 price,,,) = AggregatorV3Interface(ethPriceFeed).latestRoundData();
        uint8 decimals = AggregatorV3Interface(ethPriceFeed).decimals();
        price = price * int256(DECIMALS) / int256(10 ** decimals);
        return usd * DECIMALS / uint256(price);
    }
}

