// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import '../interfaces/ISiloOracle.sol';
import '../interfaces/IOracle.sol';

import '../oracles/ChainlinkOracle.sol';

contract MockSiloOracle is ISiloOracle {
    uint256 public constant ONE = 10**8;

    address public immutable factory;
    address public quoteToken;

    struct OracleData {
        address oracleImpl;
        bytes data;
    }

    mapping(address => uint256) public prices;
    mapping(address => OracleData) public oracles;

    modifier onlyFactory() {
        require(msg.sender == address(factory), "SiloOracle: onlyFactory");
        _;
    }

    constructor(address _quoteToken, address _factory) {
        factory = _factory;
        quoteToken = _quoteToken;
    }

    function setPrice(address _token, uint256 _price) public {
        prices[_token] = _price;
    }

    function registerToken(address _token, address _oracle, bytes memory _data) external override onlyFactory {
        require(IOracle(_oracle).isSiloOracle(), "SiloOracle: not an oracle");
        oracles[_token] = OracleData(_oracle, _data);
    }

    function getPrice(address _token) public override returns (uint256) {
        // mute warnings
        prices[_token] = prices[_token];
        return prices[_token];
    }

    function getPriceQuote(address _base, address _quote) public returns (uint256) {
        // mute warnings
        _quote;
        prices[_base] = prices[_base];
        return prices[_base];
    }
}

