// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import './interfaces/IWrappedToken.sol';
import './interfaces/ISiloBridgePool.sol';
import './interfaces/ISilo.sol';
import './interfaces/ISiloFactory.sol';

contract SiloRouter {
    using SafeERC20 for IERC20;

    IWrappedToken public immutable wrappedToken;
    ISiloBridgePool public immutable bridgePool;
    ISiloFactory public immutable factory;

    struct Position {
        // what do you use for collateral
        address collateral;
        // what do you want to borrow
        address borrow;
        // amount of collateral
        uint256 depositAmount;
        // amount to borrow
        uint256 borrowAmount;
    }

    constructor (address _factory) {
        wrappedToken = IWrappedToken(ISiloFactory(_factory).bridgeAsset());
        bridgePool = ISiloBridgePool(ISiloFactory(_factory).bridgePool());
        factory = ISiloFactory(_factory);
    }

    function borrow(Position[] memory positions) external payable {
        _transferCollateral(positions[0].collateral, positions[0].depositAmount);

        uint256 len = positions.length;
        require(len <= 2, "SiloRouter: supports only 2 positons at once");

        for (uint256 i  = 0; i < len; i++) {
            Position memory pos = positions[i];
            require(pos.collateral != pos.borrow, "SiloRouter: cannot depisit & borrow the same asset");
            if (pos.collateral == address(wrappedToken)) {
                address borrowSilo = factory.silo(pos.borrow);
                IERC20(pos.collateral).safeApprove(address(bridgePool), pos.depositAmount);
                bridgePool.depositFor(borrowSilo, msg.sender, pos.depositAmount);
                ISilo(borrowSilo).borrowFor(msg.sender, address(this), pos.borrowAmount);
            } else {
                address collateralSilo = factory.silo(pos.collateral);
                IERC20(pos.collateral).safeApprove(collateralSilo, pos.depositAmount);
                ISilo(collateralSilo).depositFor(msg.sender, pos.depositAmount);
                bridgePool.borrowFor(collateralSilo, msg.sender, address(this), pos.borrowAmount);
            }
        }

        Position memory lastPosition = positions[len - 1];
        _transferBorrow(lastPosition.borrow, lastPosition.borrowAmount);
    }

    function _transferCollateral(address _collateral, uint256 _amount) internal {
        if (_collateral == address(wrappedToken) && msg.value > 0) {
            require(msg.value == _amount, "SiloRouter: incorrect ETH value");
            wrappedToken.deposit{value: msg.value}();
        } else {
            IERC20(_collateral).safeTransferFrom(msg.sender, address(this), _amount);
        }
    }

    function _transferBorrow(address _borrow, uint256 _amount) internal {
        if (_borrow == address(wrappedToken)) {

            wrappedToken.withdraw(_amount);
            (bool success, ) = msg.sender.call{value: _amount}("");
            require(success, "SiloRouter: ETH Transfer failed.");
        } else {
            IERC20(_borrow).safeTransfer(msg.sender, _amount);
        }
    }

    receive() external payable {}
}

