pragma solidity 0.6.12;

import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol';

import '../lib/uniswapV2/UniswapV2OracleLibrary.sol';
import '../lib/uniswapV2/FixedPoint.sol';
import './Oracle.sol';

contract UniswapV2Oracle is Oracle {
    using FixedPoint for *;

    address public immutable uniswapV2Factory;

    uint256 public constant PERIOD = 60 * 15;

    struct PriceData {
        uint256 priceCumulativeLast;
        uint144 priceAverage;
        uint32 blockTimestampLast;
        uint32 genesisTimestamp;
    }

    mapping(address => mapping(address => PriceData)) internal prices;

    constructor(address _factory) public {
        uniswapV2Factory = _factory;
    }

    function initToken(address _base, address _quote, bytes memory) external override {
        update(_base, _quote, false);
    }

    function getPrice(address _base, address _quote, bytes memory) public override returns (uint256 price) {
        update(_base, _quote, true);
        return prices[_base][_quote].priceAverage;
    }

    // TODO: during first PERIOD the oracle is vulnerable, consider cool down PERIOD for prod
    function update(address _base, address _quote, bool force) internal {
        PriceData storage tokenPrice = prices[_base][_quote];
        (uint256 priceCumulative, uint32 blockTimestamp) = _currentCumulativePrices(_base, _quote);
        uint32 timeElapsed = blockTimestamp - tokenPrice.blockTimestampLast; // overflow is desired

        if (timeElapsed >= PERIOD || tokenPrice.genesisTimestamp == tokenPrice.blockTimestampLast) {

            tokenPrice.priceAverage = FixedPoint
                .uq112x112(uint224((priceCumulative - tokenPrice.priceCumulativeLast) / timeElapsed))
                .mul(1e18)
                .decode144();
            tokenPrice.priceCumulativeLast = priceCumulative;
            tokenPrice.blockTimestampLast = blockTimestamp;
            if (tokenPrice.genesisTimestamp == 0) tokenPrice.genesisTimestamp = blockTimestamp;
        }
    }

    function _currentCumulativePrices(address _base, address _quote) internal returns (uint256, uint32) {
        address pair = IUniswapV2Factory(uniswapV2Factory).getPair(_base, _quote);
        require(pair != address(0), "UniswapV2Oracle: NO_PAIR");

        (uint112 reserve0, uint112 reserve1,) = IUniswapV2Pair(pair).getReserves();
        require(reserve0 != 0 && reserve1 != 0, 'UniswapV2Oracle: NO_RESERVES');

        (uint256 price0Cumulative, uint256 price1Cumulative, uint32 blockTimestamp) = UniswapV2OracleLibrary.currentCumulativePrices(pair);
        uint256 priceCumulative;
        if (IUniswapV2Pair(pair).token0() == _base) {
            priceCumulative = price0Cumulative;
        } else {
            priceCumulative = price1Cumulative;
        }
        return (priceCumulative, blockTimestamp);
    }

}

