pragma solidity 0.8.13;

import "../interfaces/ICurvePoolLike.sol";
import "../interfaces/ICurveLPTokensPriceProviderLike.sol";
import "../interfaces/IMagician.sol";
import "../../../lib/Ping.sol";
import "../../../interfaces/IPriceProvidersRepository.sol";
