pragma solidity 0.8.7;

interface ISilo {
    function deposit(uint256 amount) external;
    function borrow(uint256 amount) external;
    function withdraw(uint256 amount) external;
    function withdrawFor(address user, uint256 amount) external;
    function repay(uint256 amount) external;
    function repayFor(address user, uint256 amount) external;
    function liquidate(address[] memory user) external returns (uint256[] memory);
    function depositFor(address user, uint256 amount) external;
    function borrowFor(address user, address to, uint256 amount) external;
    function seizeCollateral(address user, address liquidator) external returns (uint256);
    function asset() external view returns (address);
    function isSolvent(address user) external view returns (bool);
    function getCollateralValue(address user) external view returns (uint256);
}
