pragma solidity 0.8.7;

interface ISiloBridgePool {
    function depositFor(address silo, address user, uint256 amount) external;
    function borrowFor(address silo, address user, address to, uint256 amount) external;
    function seizeCollateral(address user, address liquidator) external returns (uint256);
    function addSilo(address silo, string memory name, string memory symbol) external;
    function getCollateralValue(address silo, address user) external view returns (uint256);
