pragma solidity 0.8.7;

library EasyMath {
    function abs(int256 x) internal pure returns (uint256) {
        return x <= 0 ? uint256(-x) : uint256(x);
    }

    function toShare(uint256 amount, uint256 totalAmount, uint256 totalShares) internal pure returns (uint256) {
        if (totalShares == 0 || totalAmount == 0) {
            return amount;
        }
        return amount * totalShares / totalAmount;
    }

    function toAmount(uint256 share, uint256 totalAmount, uint256 totalShares) internal pure returns (uint256) {
        if (totalShares == 0 || totalAmount == 0) {
            return 0;
        }
        return share * totalAmount / totalShares;
    }

    /// from 18 decimals to basis points (4 decimals)
    function toBasisPoint(uint256 amount) internal pure returns (uint256) {
        return amount / 1e14;
    }

    /// from basis points (4 decimals) ot 18 decimals
    function fromBasisPoint(uint256 amount) internal pure returns (uint256) {
        return amount * 1e14;
    }

    /// from 18 decimals to basis points (4 decimals)
    function toBasisPoint(int256 amount) internal pure returns (int256) {
        return amount / 1e14;
    }

    /// from basis points (4 decimals) ot 18 decimals
    function fromBasisPoint(int256 amount) internal pure returns (int256) {
        return amount * 1e14;
    }
}
