pragma solidity 0.8.7;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "./TwoStepOwnableUpgradeable.sol";
import "../interfaces/ISilo.sol";

contract BridgeERC20Upgradeable is Initializable, ERC20Upgradeable, TwoStepOwnableUpgradeable {

    ISilo public silo;

    function mint(address _account, uint256 _amount) external onlyOwner {
        _mint(_account, _amount);
    }

    function burn(address _account, uint256 _amount) external onlyOwner {
        _burn(_account, _amount);
    }

    function forceTransfer(
        address _sender,
        address _recipient,
        uint256 _amount
    ) public onlyOwner returns (bool) {
        _transfer(_sender, _recipient, _amount);
        return true;
    }

    function initialize(
        string memory _name,
        string memory _symbol,
        address _silo
    ) public initializer {
        require(_silo != address(0), "BridgeERC20: _silo != address(0)");
        __Context_init_unchained();
        __TwoStepOwnableUpgradeable_init_unchained();
        __ERC20_init_unchained(_name, _symbol);
        silo = ISilo(_silo);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        super._transfer(sender, recipient, amount);
        require(silo.isSolvent(sender), "BridgeERC20: not solvent in silo position");
    }
}
