","keccak256":"0x90565a39ae45c80f0468dc96c7b20d0afc3055f344c8203a0c9258239f350b9f","license":"MIT"},"contracts/Silo.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "./interfaces/ISilo.sol";
import "./interfaces/ISiloFactory.sol";
import "./interfaces/IGuardedLaunch.sol";
import "./interfaces/ISiloBridgePool.sol";
import "./interfaces/ISiloOracleRepository.sol";
import "./interfaces/IVirtualBalances.sol";

import "./lib/EasyMath.sol";

contract Silo is Initializable, ISilo, ERC20Upgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20 for ERC20;
    using EasyMath for uint256;

    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant INFINITY = type(uint256).max;

    // solhint-disable-next-line var-name-mixedcase
    uint256 public DECIMAL_POINTS; // TODO make it immutable
    ISiloFactory public factory;

    address public override asset;
    uint256 public totalDeposits;
    uint256 public totalBorrowShare;
    uint256 public totalBorrowAmount;
    uint256 public interestRate;
    uint256 public protocolFees;
    uint256 public borrowAmountLastUpdateTimestamp;  // TODO it might be uint32
    uint256 public interestRateLastUpdateTimestamp;  // TODO it might be uint32

    mapping(address => uint256) public borrowShare;

    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);
    event Borrow(address indexed user, uint256 amount);
    event Repay(address indexed user, uint256 amount);
    event Liquidate(address indexed _user, uint256 amountRepaid, uint256 seizedCollateral);

    modifier onlyRouter() {
        require(msg.sender == factory.router(), "Silo: onlyRouter");
        _;
    }

    modifier onlyBridge() {
        require(msg.sender == factory.bridgePool(), "Silo: onlyBridge");
        _;
    }

    modifier interest() {
        accrueInterest();
        _;
    }

    /// @dev check if user has enough collateral deposited to silo to support withstanding borrow position on bridgePool
    modifier bridgePositionSolvent(address _user) {
        _;
        ISiloBridgePool bridgePool = ISiloBridgePool(factory.bridgePool());
        require(bridgePool.isSolvent(address(this), _user), "Silo: not solvent in bridge position");
    }

    modifier validateBorrow(address _user) {
        _;
        require(
            getLTV(_user) <= factory.getMaximumLTV(address(this)),
            "Silo: maximumLTV"
        );
    }

    modifier validateMaxDeposits() {
        _;
        ISiloOracleRepository oracle = ISiloOracleRepository(factory.oracle());
        require(
            oracle.getPrice(asset) * totalDeposits / DECIMAL_POINTS <=
                IGuardedLaunch(address(factory)).getMaxSiloDeposits(address(this)),
            "Silo: total deposits exceed liquidity limit"
        );
    }

    function deposit(uint256 _amount) external override {
        _deposit(msg.sender, msg.sender, _amount);
    }

    function depositFor(address _user, uint256 _amount) external override onlyRouter {
        _deposit(_user, msg.sender, _amount);
    }

    function withdraw(uint256 _amount) external override {
        _withdraw(msg.sender, _amount);
    }

    function withdrawFor(address _user, uint256 _amount) external override onlyRouter {
        _withdraw(_user, _amount);
    }

    function borrow(uint256 _amount) external override validateBorrow(msg.sender) interest  {
        _borrow(msg.sender, msg.sender, _amount);
    }

    function borrowFor(address _user, address _to, uint256 _amount)
        external override onlyRouter validateBorrow(_user) interest
    {
        _borrow(_user, _to, _amount);
    }

    function repay(uint256 _amount) external override nonReentrant {
        _repay(msg.sender, msg.sender, _amount);
    }

    function repayFor(address _user, uint256 _amount) external override nonReentrant onlyRouter {
        _repay(_user, msg.sender, _amount);
    }

    function seizeCollateral(address _user, address _liquidator) external override onlyBridge returns (uint256) {
        uint256 collateral = balanceOf(_user);
        _transfer(_user, _liquidator, collateral);
        return collateral.toAmount(totalDeposits, totalSupply());
    }

    function liquidate(address[] memory _users) external override nonReentrant interest returns (uint256[] memory) {
        uint256[] memory liquidated = new uint256[](_users.length);
        uint256 n = _users.length;
        for (uint256 i = 0; i < n; i++) {
            liquidated[i] = _liquidate(_users[i]);
        }
        return liquidated;
    }

    function initialize(
        address _asset,
        string memory _name,
        string memory _symbol
    ) public initializer {
        __Context_init_unchained();
        __ReentrancyGuard_init_unchained();
        __ERC20_init_unchained(_name, _symbol);
        factory = ISiloFactory(msg.sender);
        asset = _asset;
        DECIMAL_POINTS = 10**ERC20(_asset).decimals();
    }

    /// @dev should always calculate all interest based on current timestamp, regardless of the intervals.
    ///      Intervals should be used only to change interest rate.
    function accrueInterest() public {
        uint256 borrowAmountLastUpdateTimestampCached = borrowAmountLastUpdateTimestamp;
        uint256 totalDepositsCached = totalDeposits;
        uint256 blockTimestamp = block.timestamp;

        if (blockTimestamp == borrowAmountLastUpdateTimestampCached) {
            return;
        }

        address model = factory.getVirtualBalances(address(this));

        IVirtualBalances.VirtualBalancesOutput memory output = IVirtualBalances(model).getInterestData(
            IVirtualBalances.VirtualBalancesInputData({
                currentTimestamp: blockTimestamp,
                deposit: totalDepositsCached,
                siloShareFee: BASIS_POINTS - factory.protocolShareFee(),
                borrowAmount: totalBorrowAmount,
                borrowAmountLastUpdateTimestamp: borrowAmountLastUpdateTimestampCached,
                interestRate: interestRate,
                interestRateLastUpdateTimestamp: interestRateLastUpdateTimestamp
            })
        );

        totalBorrowAmount = output.newBorrowAmount;
        borrowAmountLastUpdateTimestamp = output.newBorrowAmountUpdateTimestamp;
        interestRate = output.newInterestRate;
        interestRateLastUpdateTimestamp = output.newInterestRateTimestamp;

        // accrue interest
        (
            protocolFees,
            totalDeposits
        ) = calculateInterestShares(protocolFees, totalDepositsCached, output.totalInterests);
    }

    function getTotalDepositsUpdated() public view returns (uint256 _totalDeposits) {
        IVirtualBalances.VirtualBalancesOutput memory output = _executeInterestRateCalculations();
        (,_totalDeposits) = calculateInterestShares(protocolFees, totalDeposits, output.totalInterests);
    }

    function isSolvent(address _user) public view override returns (bool) {
        require(_user != address(0), "Silo: user != address(0)");
        uint256 liquidationThreshold = factory.getLiquidationThreshold(address(this));
        return getLTV(_user) <= liquidationThreshold;
    }

    function getLTV(address _user) public view returns (uint256) {
        uint256 borrowAmountValue = getBorrowAmountValue(_user);
        if (borrowAmountValue == 0) return 0;

        ISiloBridgePool bridgePool = ISiloBridgePool(factory.bridgePool());
        uint256 collateralValue = bridgePool.getCollateralValue(address(this), _user);
        if (collateralValue == 0) return INFINITY;
        return borrowAmountValue * BASIS_POINTS / collateralValue;
    }

    /// @dev value of collateral (asset) provided by user
    function getCollateralValue(address _user) public view override returns (uint256) {
        ISiloOracleRepository oracle = ISiloOracleRepository(factory.oracle());
        uint256 assetPrice = oracle.getPrice(asset);
        uint256 assetAmount = balanceOf(_user).toAmount(getTotalDepositsUpdated(), totalSupply());
        return assetAmount * assetPrice / DECIMAL_POINTS;
    }

    /// @dev value of asset borrowed by user
    function getBorrowAmountValue(address _user) public view returns (uint256) {
        ISiloOracleRepository oracle = ISiloOracleRepository(factory.oracle());

        // TODO can we save gas if we do not return whole structure?
        IVirtualBalances.VirtualBalancesOutput memory output = _executeInterestRateCalculations();

        uint256 assetDebtAmount = borrowShare[_user].toAmount(output.newBorrowAmount, totalBorrowShare);
        uint256 assetPrice = oracle.getPrice(asset);
        return assetDebtAmount * assetPrice / DECIMAL_POINTS;
    }

    function liquidity() public view returns (uint256) {
        return ERC20(asset).balanceOf(address(this));
    }

    function balanceOfUnderlying(address _user) public view returns (uint256) {
        uint256 share = balanceOf(_user);
        return share.toAmount(getTotalDepositsUpdated(), totalSupply());
    }

    function calcFee(uint256 _amount) public view returns (uint256) {
        uint256 entryFee = factory.entryFee();
        if (entryFee == 0) return 0; // no fee
        return _amount * entryFee / BASIS_POINTS;
    }

    function calculateInterestShares(
        uint256 _protocolFees,
        uint256 _totalDeposits,
        uint256 _totalInterestsForDeltaTime
    ) public view returns (
        uint256 newProtocolFees,
        uint256 newTotalDeposits
    ) {
        uint256 protocolInterestShare = _totalInterestsForDeltaTime * factory.protocolShareFee() / BASIS_POINTS;
        uint256 siloInterestShare = _totalInterestsForDeltaTime - protocolInterestShare;

        newProtocolFees = _protocolFees + protocolInterestShare;
        newTotalDeposits = _totalDeposits + siloInterestShare;
    }

    function _deposit(address _user, address _from, uint256 _amount)
        internal
        nonReentrant
        interest
        validateMaxDeposits
    {
        require(borrowShare[_user] == 0, "Silo: cannot deposit & borrow the same asset");

        uint256 balanceBefore = liquidity();
        ERC20(asset).safeTransferFrom(_from, address(this), _amount);
        uint256 balanceAfter = liquidity();

        _amount = balanceAfter - balanceBefore;
        uint256 share = _amount.toShare(totalDeposits, totalSupply());
        totalDeposits += _amount;
        emit Deposit(_user, _amount);
        _mint(_user, share);
    }

    function _withdraw(address _user, uint256 _amount) internal nonReentrant bridgePositionSolvent(_user) interest {
        uint256 share;
        if (_amount == type(uint256).max) {
            share = balanceOf(_user);
            _amount = share.toAmount(totalDeposits, totalSupply());
        } else {
            share = _amount.toShare(totalDeposits, totalSupply());
        }
        totalDeposits -= _amount;
        _burn(_user, share);
        emit Withdraw(_user, _amount);

        ERC20(asset).safeTransfer(_user, _amount);
    }

    function _borrow(address _user, address _to, uint256 _amount) internal nonReentrant {
        require(balanceOf(_user) == 0, "Silo: cannot borrow & deposit the same asset");
        require(liquidity() >= _amount, "Silo: deposit shortage");

        uint256 fee = calcFee(_amount);
        // TODO, do we need to apply virtual balances here??

        uint256 borrowerShare = (_amount + fee).toShare(totalBorrowAmount, totalBorrowShare);
        borrowShare[_user] += borrowerShare;
        totalBorrowShare += borrowerShare;
        totalBorrowAmount += _amount + fee;
        protocolFees += fee;

        emit Borrow(_user, _amount);

        ERC20(asset).safeTransfer(_to, _amount);
    }

    function _repay(address _user, address _from, uint256 _amount) internal interest returns (uint256) {
        uint256 repayShare;
        if (_amount == type(uint256).max) {
            repayShare = borrowShare[_user];
            _amount = repayShare.toAmount(totalBorrowAmount, totalBorrowShare);
        } else {
            repayShare = _amount.toShare(totalBorrowAmount, totalBorrowShare);
        }

        borrowShare[_user] -= repayShare;
        totalBorrowShare -= repayShare;
        totalBorrowAmount -= _amount;

        emit Repay(_user, _amount);

        ERC20(asset).safeTransferFrom(_from, address(this), _amount);

        //  TODO what about change? if somebody repays more than should

        return _amount;
    }

    function _liquidate(address _user) internal returns (uint256 seizedCollateral) {
        // gracefully fail if _user is solvent
        if (isSolvent(_user)) return 0;

        uint256 amountRepaid = _repay(_user, msg.sender, type(uint256).max);

        ISiloBridgePool bridgePool = ISiloBridgePool(factory.bridgePool());
        seizedCollateral = bridgePool.seizeCollateral(_user, msg.sender);

        emit Liquidate(_user, amountRepaid, seizedCollateral);

        require(isSolvent(_user), "Silo: user not solvent");
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override bridgePositionSolvent(sender) {
        super._transfer(sender, recipient, amount);
    }

    function _executeInterestRateCalculations()
        internal
        view
        returns (IVirtualBalances.VirtualBalancesOutput memory output)
    {
        address model = factory.getVirtualBalances(address(this));

        return IVirtualBalances(model).getInterestData(
            IVirtualBalances.VirtualBalancesInputData({
                currentTimestamp: block.timestamp,
                deposit: totalDeposits,
                siloShareFee: BASIS_POINTS - factory.protocolShareFee(),
                borrowAmount: totalBorrowAmount,
                borrowAmountLastUpdateTimestamp: borrowAmountLastUpdateTimestamp,
                interestRate: interestRate,
                interestRateLastUpdateTimestamp: interestRateLastUpdateTimestamp
            })
        );
    }
}
