pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./interfaces/ISiloFactory.sol";
import "./interfaces/ISiloBridgePool.sol";
import "./interfaces/ISiloOracleRepository.sol";
import "./interfaces/IVirtualBalances.sol";

import "./utils/GuardedLaunch.sol";
import "./Silo.sol";

contract SiloFactory is ISiloFactory, GuardedLaunch {
    /// @dev SiloConfig struct represents configurable parameters for each Silo
    struct SiloConfig {
        /// @dev Loan-to-Value ratio represents the maximum borrowing power of a specific collateral.
        ///      For example, if a collateral has a LTV of 75%, the user can borrow up to 0.75 worth
        ///      of ETH in the principal currency for every 1 ETH worth of collateral.
        uint256 maxLoanToValue;

        /// @dev Liquidation Threshold represents the threshold at which a borrow position will be considered
        ///      undercollateralized and subject to liquidation for each collateral. For example,
        ///      if a collateral has a liquidation threshold of 80%, it means that the loan will be
        ///      liquidated when the borrowAmount value is worth 80% of the collateral value.
        uint256 liquidationThreshold;

        address virtualBalances;
    }

    uint256 public constant BASIS_POINTS = 10000;
    //Implementation of Silo for cloning
    address public immutable siloImplementation;

    /// @dev Default values for SiloConfig. Used if values are not configured manually.
    SiloConfig public defaultSiloConfig;
    address public override bridgeAsset;

    /// @dev one time protocol fee for opening a borrow position in basis points
    uint256 public override entryFee;
    /// @dev protocol revenue share in interest paid
    uint256 public override protocolShareFee;
    /// @dev protocol share in liquidation profit
    uint256 public protocolLiquidationFee;

    address public override bridgePool;
    address public override oracle;
    address public override router;

    /// @dev maps token address to silo address, token => silo
    mapping(address => address) public override silo;
    /// @dev maps token address to silo address, silo => token
    mapping(address => address) public siloReverse;
    /// @dev maps silo address to its config
    mapping(address => SiloConfig) public siloConfigs;

    event NewSilo(address indexed silo, address indexed asset, string name, string symbol);
    event InterestRateModel(address oldModel, address newModel);
    event BridgePoolUpdate(address oldPool, address newPool);
    event OracleUpdate(address oldOracle, address newOracle);
    event RouterUpdate(address oldRouter, address newRouter);
    event FeeUpdate(
        uint256 oldEntryFee,
        uint256 newEntryFee,
        uint256 oldProtocolShareFee,
        uint256 newProtocolShareFee,
        uint256 oldProtocolLiquidationFee,
        uint256 newProtocolLiquidationFee
    );

    constructor(
        uint256 _defaultMaxLTV,
        uint256 _defaultLiquidationThreshold,
        address _bridgeAsset
    ) {
        __TwoStepOwnableUpgradeable_init();
        defaultSiloConfig.maxLoanToValue = _defaultMaxLTV;
        defaultSiloConfig.liquidationThreshold = _defaultLiquidationThreshold;
        bridgeAsset = _bridgeAsset;
        Silo siloImpl = new Silo();
        siloImpl.initialize(_bridgeAsset, "Silo", "silo");
        siloImplementation = address(siloImpl);
    }

    function setFees(uint256 _entryFee, uint256 _protocolShareFee, uint256 _protocolLiquidationFee) external onlyOwner {
        emit FeeUpdate(
            entryFee,
            _entryFee,
            protocolShareFee,
            _protocolShareFee,
            protocolLiquidationFee,
            _protocolLiquidationFee
        );

        entryFee = _entryFee;
        protocolShareFee = _protocolShareFee;
        protocolLiquidationFee = _protocolLiquidationFee;
    }

    function setSiloConfig(
        address _silo,
        uint256 _maxLoanToValue,
        uint256 _liquidationThreshold,
        address _interestRateModel
    ) external onlyOwner {
        siloConfigs[_silo] = SiloConfig(_maxLoanToValue, _liquidationThreshold, _interestRateModel);
    }

    function setDefaultVirtualBalances(address _defaultInterestRateModel) external onlyOwner {
        require(_defaultInterestRateModel != address(0), "SiloFactory: new defaultInterestRateModel is zero address");
        emit InterestRateModel(defaultSiloConfig.virtualBalances, _defaultInterestRateModel);
        defaultSiloConfig.virtualBalances = _defaultInterestRateModel;
    }

    function setDefaultMaximumLTV(uint256 _defaultMaxLTV) external onlyOwner {
        defaultSiloConfig.maxLoanToValue = _defaultMaxLTV;
    }

    function setDefaultLiquidationThreshold(uint256 _defaultLiquidationThreshold) external onlyOwner {
        defaultSiloConfig.liquidationThreshold = _defaultLiquidationThreshold;
    }

    function setBridgePool(address _bridgePool) external onlyOwner {
        require(_bridgePool != address(0), "SiloFactory: new bridgePool is zero address");
        emit BridgePoolUpdate(bridgePool, _bridgePool);
        bridgePool = _bridgePool;
    }

    function setOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "SiloFactory: new oracle is zero address");
        emit OracleUpdate(oracle, _oracle);
        oracle = _oracle;
    }

    function setRouter(address _router) external onlyOwner {
        require(_router != address(0), "SiloFactory: new router is zero address");
        emit RouterUpdate(router, _router);
        router = _router;
    }

    function isSilo(address _silo) external view override returns (bool) {
        return siloReverse[_silo] != address(0);
    }

    function getMaximumLTV(address _silo) external view override returns (uint256) {
        uint256 maxLoanToValue = siloConfigs[_silo].maxLoanToValue;
        if (maxLoanToValue != 0) {
            return maxLoanToValue;
        }
        return defaultSiloConfig.maxLoanToValue;
    }

    function getVirtualBalances(address _silo) external view override returns (address) {
        address interestRateModel = siloConfigs[_silo].virtualBalances;
        if (interestRateModel != address(0)) {
            return interestRateModel;
        }
        return defaultSiloConfig.virtualBalances;
    }

    function getLiquidationThreshold(address _silo) external view override returns (uint256) {
        uint256 liquidationThreshold = siloConfigs[_silo].liquidationThreshold;
        if (liquidationThreshold != 0) {
            return liquidationThreshold;
        }
        return defaultSiloConfig.liquidationThreshold;
    }

    /// @param _asset silo asset
    /// @param _oracle (optional) if provided, asset will be initialised on that oracle
    ///        it does not mean, this oracle will be chosen for asset, it only means, after valid initialization,
    ///        it could be chosen, if is is the best one
    /// @param _oracleData custom data for oracle initialization
    function newSilo(address _asset, address _oracle, bytes calldata _oracleData) public isPermissionedSiloCreation {
        require(_asset != bridgeAsset, "SiloFactory: bridge asset");
        require(silo[_asset] == address(0), "SiloFactory: silo exists");

        string memory name = string(abi.encodePacked(string("Silo "), ERC20(_asset).name()));
        string memory symbol = string(abi.encodePacked(string("silo"), ERC20(_asset).symbol()));

        address _silo = Clones.clone(siloImplementation);
        Silo(_silo).initialize(_asset, name, symbol);
        silo[_asset] = _silo;
        siloReverse[_silo] = _asset;
        emit NewSilo(_silo, _asset, name, symbol);

        ISiloBridgePool(bridgePool).addSilo(_silo, name, symbol);
        ISiloOracleRepository(oracle).initOracleForAsset(_asset, _oracle, _oracleData);
    }
}
