pragma solidity 0.8.7;

interface IVirtualBalances {
    /// @param currentTimestamp block.timestamp
    /// @param siloShareFee BASIS_POINTS - factory.protocolShareFee
    /// @param deposit total deposit of the asset
    /// @param borrowAmount total borrow amount
    /// @param borrowAmountLastUpdateTimestamp time when `borrowAmount` was updated
    /// @param interestRate interest rate at `interestRateLastUpdateTimestamp`
    /// @param interestRateLastUpdateTimestamp time when `interestRate` was updated
    struct VirtualBalancesInputData {
        uint256 currentTimestamp;
        uint256 siloShareFee;
        uint256 deposit;
        uint256 borrowAmount;
        uint256 borrowAmountLastUpdateTimestamp;
        uint256 interestRate;
        uint256 interestRateLastUpdateTimestamp;
    }

    /// @param newDeposit new deposit after apply interests for interval
    /// @param newBorrowAmount new borrowAmount after apply interests for interval
    /// @param newBorrowAmountUpdateTimestamp timestamp for `newBorrowAmount`
    /// @param newInterestRate new current interest rate
    /// @param newInterestRateTimestamp timestamp for `newInterestRate`
    /// @param totalInterests interest for provided period of time
    ///        from `borrowAmountLastUpdateTimestamp` to `newBorrowAmountUpdateTimestamp`
    /// @param protocolInterestShare % for protocol
    /// @param siloInterestShare % for users
    struct VirtualBalancesOutput {
        uint256 newDeposit;
        uint256 newBorrowAmount;
        uint256 newBorrowAmountUpdateTimestamp;
        uint256 newInterestRate;
        uint256 newInterestRateTimestamp;
        uint256 totalInterests;
        uint256 protocolInterestShare;
        uint256 siloInterestShare;
    }

    /// @notice calculates total interests since `_lastUpdateTimestamp`
    /// @param _params see VirtualBalancesInputData
    /// @return output see InterestDataOuptut
    function getInterestData(VirtualBalancesInputData memory _params)
        external view returns (VirtualBalancesOutput memory output);
}
