pragma solidity 0.8.7;

import "./interfaces/ISiloOracleRepository.sol";
import "./interfaces/IOracle.sol";
import "./utils/TwoStepOwnableUpgradeable.sol";

contract SiloOracleRepository is ISiloOracleRepository, TwoStepOwnableUpgradeable {
    uint256 public constant ONE = 10**18;

    address public immutable factory;
    address public immutable quoteToken;

    mapping(address => bool) public registeredOracles;
    mapping(address => address) public assetsOracles;

    address[] public oracles;

    event NewOracle(address newOracle, uint256 id);
    event OracleRemoved(address oracle);
    event OracleForAsset(address oracle, address asset);

    modifier onlyFactory() {
        require(msg.sender == address(factory), "SiloOracleRepository: onlyFactory");
        _;
    }

    constructor(address _quoteToken, address _factory) {
        __TwoStepOwnableUpgradeable_init();
        factory = _factory;
        quoteToken = _quoteToken;
    }

    function addOracle(address _oracle) external override onlyOwner {
        require(!registeredOracles[_oracle], "SiloOracleRepository: oracle already exists");
        require(IOracle(_oracle).isSiloOracle(), "SiloOracleRepository: not an oracle");
        require(IOracle(_oracle).quoteToken() == quoteToken, "SiloOracleRepository: invalid quoteToken in oracle");

        emit NewOracle(_oracle, oracles.length);

        registeredOracles[_oracle] = true;
        oracles.push(_oracle);
    }

    function removeOracle(uint256 _oracleId) external override onlyOwner {
        address oracleToRemove = oracles[_oracleId];
        require(registeredOracles[oracleToRemove], "SiloOracleRepository: oracle does not exist");

        emit OracleRemoved(oracleToRemove);

        registeredOracles[oracleToRemove] = false;
        oracles[_oracleId] = oracles[oracles.length - 1];
        oracles.pop();
    }

    /// @notice initialise oracle for asset
    /// @dev this method does two things:
    ///      - init new asset if oracle is provided
    ///      - search for best oracle and updates if we found better
    /// @param _asset address od token to be initialise
    /// @param _oracle (optional) address of silo oracle that will be initialised with new asset
    ///        we do not check, if this oracle is registered or not, because it can not be used if not registered anyway
    /// @param _data (optional) custom data required for asset initialisation
    function initOracleForAsset(address _asset, address _oracle, bytes calldata _data) external override {
        if (_oracle != address(0)) {
            IOracle(_oracle).initAsset(_asset, _data);
        }

        _findBestOracleForAsset(_asset);
    }

    function setBestOracleForAsset(address _asset) external override returns (address bestOracle) {
        return _findBestOracleForAsset(_asset);
    }

    function changeBestOracleForAsset(address _asset, address _oracle) external virtual override {
        require(registeredOracles[_oracle], "SiloOracleRepository: oracle not registered");

        address assetOracles = assetsOracles[_asset];
        require(assetOracles != address(0), "SiloOracleRepository: use setBestOracleForAsset to init asset");

        uint256 currentLiquidity = IOracle(assetOracles).getQuoteLiquidity(_asset);
        uint256 suggestedLiquidity = IOracle(_oracle).getQuoteLiquidity(_asset);
        require(suggestedLiquidity >= currentLiquidity, "SiloOracleRepository: chosen oracle is worse");

        _setOracleForAsset(_asset, _oracle);
    }

    function findBestOracle(address _asset) external view returns (address) {
        return _findBestOracle(_asset);
    }

    function oraclesCount() external view returns (uint256) {
        return oracles.length;
    }

    function oracleList() external view returns (address[] memory) {
        return oracles;
    }

    function getPrice(address _base) public view override virtual returns (uint256) {
        if (_base == quoteToken) return ONE;

        return IOracle(assetsOracles[_base]).getPrice(_base);
    }

    /// @notice search for best oracle for asset
    function _findBestOracleForAsset(address _asset) internal returns (address bestOracle) {
        bestOracle = _findBestOracle(_asset);
        require(bestOracle != address(0), "SiloOracleRepository: can not find oracle for asset");

        _setOracleForAsset(_asset, bestOracle);
    }

    function _setOracleForAsset(address _asset, address _oracle) internal {
        emit OracleForAsset(_asset, _oracle);
        IOracle(_oracle).notifyAboutChoice(_asset);
        assetsOracles[_asset] = _oracle;
    }

    function _findBestOracle(address _asset) internal view returns (address bestOracle) {
        uint256 maxLiquidity;

        for (uint256 i = 0; i < oracles.length; i++) {
            address oracle = oracles[i];
            uint256 quoteLiquidity = IOracle(oracle).getQuoteLiquidity(_asset);

            if (quoteLiquidity > maxLiquidity) {
                bestOracle = oracle;
                maxLiquidity = quoteLiquidity;
            }
        }
    }
}
