pragma solidity 0.8.7;

import "./lib/EasyMath.sol";

contract InterestRateModel {
    using EasyMath for int256;

    uint256 public constant SECONDS_PER_YEAR = 31536000;
    uint256 public constant BASIS_POINTS = 10000;

    uint256 public constant ACCEPTED_RANGE = 5e16;
    int256 public constant DECIMAL_POINTS = 1e18;

    /// @dev is our target value for borrowAmount utilization
    ///         eg. if optimal utilization is X, then if current utilization is less than X,
    ///         interest rate will be dropping to give incentive to borrow and bring utilization closer to X
    ///         when current utilization will be higher than X, interests will be increased to give incentive for return
    ///         money and bring utilization closer to X
    ///         utilization is using DECIMAL_POINTS, so base unit is 1e18
    int256 public immutable pidUtilizationOptimal;

    int256 public immutable pidKp;

    /// @dev interval for interest rate update in seconds
    uint256 public immutable interestsRateInterval;

    constructor(
        uint64 _pidUtilizationOptimal,
        uint64 _pidKp,
        uint64 _interestsRateInterval
    ) {
        require(_pidUtilizationOptimal != 0, "InterestRateModel: invalid _utilizationOptimal");
        require(_pidKp != 0, "InterestRateModel: invalid _pidKp");
        require(_interestsRateInterval != 0, "InterestRateModel: invalid _updateInterval");

        pidUtilizationOptimal = int256(uint256(_pidUtilizationOptimal));
        pidKp = int256(uint256(_pidKp));
        interestsRateInterval = uint256(_interestsRateInterval);
    }

    /// @notice calculates new interest rate
    /// @param _deposit total deposit of the asset
    /// @param _borrowAmount total borrow amount
    /// @param _interestRate interest rate at `_interestRateUpdateTimestamp`
    /// @return newInterestRate new current interest rate, it will be changed ONLY if `_deltaTime` >= `_interval`
    function interestRateForInterval(uint256 _deposit, uint256 _borrowAmount, uint256 _interestRate)
        public
        view
        returns (uint256 newInterestRate)
    {
        unchecked {
            // borrowAmountUtilization
            int256 u = int256(_borrowAmount) * DECIMAL_POINTS / int256(_deposit);

            int256 error = u - pidUtilizationOptimal;

            if (error.abs() <= ACCEPTED_RANGE) {
                error = 0;
            }

            int256 p = pidKp * error / DECIMAL_POINTS;
            int256 interestRate = int256(_interestRate) + p.toBasisPoint();

            if (interestRate < 0) {
                interestRate = 0;
            }

            return uint256(interestRate);
        }
    }

    function calculatePastInterests(
        uint256 _borrowAmountWithInterests,
        uint256 _interestRate,
        uint256 _deltaTime
    ) public pure returns (uint256 pastInterests) {
        uint256 x = _interestRate * _deltaTime;
        pastInterests = _borrowAmountWithInterests * x / (BASIS_POINTS * SECONDS_PER_YEAR + x);
    }

    function calculateInterests(uint256 _borrowAmount, uint256 _interestRate, uint256 _siloShareFee, uint256 _deltaTime)
        public
        pure
        returns (uint256 interests, uint256 siloInterestShare)
    {
        uint256 fullInterests = _borrowAmount * _interestRate * _deltaTime;

        unchecked {
            interests = fullInterests / BASIS_POINTS / SECONDS_PER_YEAR;
            siloInterestShare = interests * _siloShareFee / BASIS_POINTS;
        }
    }
}
