pragma solidity 0.8.7;

import "./InterestRateModel.sol";
import "./interfaces/IVirtualBalances.sol";

// import "hardhat/console.sol";

contract VirtualBalances is IVirtualBalances, InterestRateModel {
    /// @dev max iterations for calculating virtual interests rates, see `getInterestRate()`
    ///         interests rates can be different for each interval, they are calculated in a loop
    ///         in order to not run out of gas and do not lock silo, we need to stop iterating and apply flat rate
    uint256 public immutable iterationsBeforeFlatRate;

    constructor(
        uint64 _pidUtilizationOptimal,
        uint64 _pidKp,
        uint64 _interestsRateInterval,
        uint64 _iterationsBeforeFlatRate
    ) InterestRateModel(_pidUtilizationOptimal, _pidKp, _interestsRateInterval) {
        require(_iterationsBeforeFlatRate != 0, "VirtualBalances: invalid _intervalIterationsForFlatRate");

        iterationsBeforeFlatRate = uint256(_iterationsBeforeFlatRate);
    }

    /// @dev this is helper for gas calculations
    function calcGasForInterestData(VirtualBalancesInputData calldata _input) external {
        _getInterestData(_input);
    }

    /// @inheritdoc IVirtualBalances
    function getInterestData(VirtualBalancesInputData calldata _input)
        external view override returns (VirtualBalancesOutput memory output)
    {
        output = _getInterestData(_input);
        output.newBorrowAmountUpdateTimestamp = _input.currentTimestamp;
    }

    function preIterationCalculations(VirtualBalancesInputData calldata _input) public view returns (
        uint256 newDeposit,
        uint256 newBorrowAmount,
        uint256 interestRateTimestampAfterIterations,
        uint256 maxIterations
    ) {
        unchecked {
            require(
                _input.borrowAmountLastUpdateTimestamp - _input.interestRateLastUpdateTimestamp < interestsRateInterval,
                "_calculateDebtAndInterest: invalid timestamps"
            );

            uint256 timePassed = _input.borrowAmountLastUpdateTimestamp - _input.interestRateLastUpdateTimestamp;
            uint256 passedInterests = calculatePastInterests(_input.borrowAmount, _input.interestRate, timePassed);
            uint256 siloPassedInterests  = passedInterests * _input.siloShareFee / BASIS_POINTS;

            // when we subtract this interests, we can do full iterations for first period
            newBorrowAmount = _input.borrowAmount - passedInterests;
            newDeposit = _input.deposit - siloPassedInterests;

            // at this moment we know, we will do at least one iteration, so:
            // _input.currentTimestamp is >= _input.interestRateLastUpdateTimestamp + iteration

            uint256 borrowAmountFullDeltaTime = _input.currentTimestamp - _input.interestRateLastUpdateTimestamp;
            maxIterations = borrowAmountFullDeltaTime / interestsRateInterval;
            // console.log("%s - %s", _input.currentTimestamp, _input.interestRateLastUpdateTimestamp);
            // console.log("borrowAmountFullDeltaTime %s, max: %s", borrowAmountFullDeltaTime, maxIterations);

            if (maxIterations > iterationsBeforeFlatRate) {
                maxIterations = iterationsBeforeFlatRate;
            }

            return (
                newDeposit,
                newBorrowAmount,
                _input.interestRateLastUpdateTimestamp + maxIterations * interestsRateInterval,
                maxIterations
            );
        }
    }

    /// @dev should always calculate all interest, regardless of the intervals.
    ///      Intervals should be used only to change interest rate.
    function _getInterestData(VirtualBalancesInputData calldata _input)
        internal
        view
        returns (VirtualBalancesOutput memory output)
    {
        unchecked {
            if (_input.borrowAmount == 0) {
                output.newInterestRateTimestamp =
                    _input.currentTimestamp - (_input.currentTimestamp % interestsRateInterval);

                output.newDeposit = _input.deposit;

                return output;
            }

            if (_input.deposit == 0) {
                // TODO this is still weird case, is it possible to have _borrowAmount > 0 and _deposit == 0?
                //      idk what should be correct values here
                output.newBorrowAmount = _input.borrowAmount;
                output.newInterestRate = _input.interestRate;
                output.newInterestRateTimestamp = _input.interestRateLastUpdateTimestamp;

                return output;
            }

            (
                output.newDeposit,
                output.newBorrowAmount,
                output.newInterestRate,
                output.newInterestRateTimestamp
            ) = _calculateDebtAndInterest(_input);

            require(
                _input.borrowAmount <= output.newBorrowAmount,
                "VirtualBalances._getInterestRate: borrowAmount overflow"
            );

            output.totalInterests = output.newBorrowAmount - _input.borrowAmount;
        }
    }

    // TODO return number of iterations?
    // solhint-disable-next-line function-max-lines
    function _calculateDebtAndInterest(VirtualBalancesInputData calldata _input) internal view returns (
        uint256 newDeposit,
        uint256 newBorrowAmount,
        uint256 newInterestRate,
        uint256 newInterestRateTimestamp
    ) {
        uint256 interestRateTimestampAfterIterations;
        uint256 maxIterations;
        newInterestRate = _input.interestRate;

        (
            newDeposit,
            newBorrowAmount,
            interestRateTimestampAfterIterations,
            maxIterations
        ) = preIterationCalculations(_input);

        // TODO test how much iterations it can handle but not on its own - inside tx when we will be updating interests
        // TODO is recurrence more optimal way?
        // we doing "full" iterations, means every deltaTime is equal to interestsRateInterval
        while (maxIterations != 0) {
            unchecked {
                maxIterations--;

                (uint256 interests, uint256 siloInterestShare) = calculateInterests(
                    newBorrowAmount,
                    newInterestRate,
                    _input.siloShareFee,
                    interestsRateInterval
                );

                newBorrowAmount += interests;
                newDeposit += siloInterestShare;
                newInterestRate = interestRateForInterval(_input.deposit, newBorrowAmount, newInterestRate);
            }
        }

        uint256 timeLeft = _input.currentTimestamp - interestRateTimestampAfterIterations;

        // we need to handle last step, it might be:
        // - because we have flat rate
        // - because last iteration is not for full interval
        if (timeLeft != 0) {
            (uint256 interests, uint256 siloInterestShare) = calculateInterests(
                newBorrowAmount,
                newInterestRate,
                _input.siloShareFee,
                timeLeft
            );

            newBorrowAmount += interests;
            newDeposit += siloInterestShare;
        }

        newInterestRateTimestamp = _input.currentTimestamp - (_input.currentTimestamp % interestsRateInterval);
    }
}
